package JavaPrograms;

public class PositiveNegativeZero {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=10;
		if(num>0) {
			System.out.println("Positive");
		}
		else if(num==0) {
			System.out.println("Zero");
		}
		else {
			System.out.println("Negative");
		}

	}

}
