package JavaPrograms;

public class SimpleIfElseIfStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String city="Kolkata";
		if(city=="Delhi") {
			System.out.println("Your City is: "+ city);
		}
		else if(city=="Mumbai") {
			System.out.println("Your City is: "+ city);
		}
		else if(city=="Kolkata") {
			System.out.println("Your City is: "+ city);
		}
		else if(city=="Chennai") {
			System.out.println("Your City is: "+ city);
		}
		else {
			System.out.println("Your City is: "+ city);
		}
	}

}
