package JavaPrograms;

public class Widening {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a=10;//Initialized variable int a
        float f=a;//assigned the value of a to f
        System.out.println(f);//printing the value of f
        System.out.println(a);//printing the value of a
        System.out.println((float)a);//typecasting the variable a to float from int
	}

}
